      onRunNewSearch(transformedQuery, boostConfig)
        .then(() => {
          setSearchLoading(false);
        })
        .catch(err => {
          console.error("Error running new search:", err);
          setError("Failed to run new search: " + (err.message || 'Unknown error'));
          setSearchLoading(false);
        });
    } else {
